pragma solidity ^0.8.0;

import "../abstract/Campaign.sol";

/**
 * @title EthCampaign
 */
contract EthCampaign is Campaign {
    event ValueIn(address provider, uint256 amount);

    function initEthCampaign(
        bytes32 _sharesMerkleRoot,
        bytes32 _strategyUri,
        address _guardian,
        address _oracle
    ) public {
